import NavBar from './components/NavBar.vue';
import newsSection from './components/newsSection.vue';
import ProjFooter from './components/ProjFooter.vue';
export default {
  name: 'App',
  components: {
    NavBar,
    newsSection,
    ProjFooter
  },

  data() {
    return {
      newsFetch: [],
      news: []
    };
  },

  methods: {
    async fetchNews() {
      const response = await fetch("http://localhost:4455/db.json");
      const data = await response.json(); //console.log(data);

      return data;
    }

  },

  async created() {
    this.newsFetch = await this.fetchNews();
    console.log("fetch successful", this.newsFetch);
    this.news = this.newsFetch.news;
  }

};
